mapper.update:
update

set 
goods_count = #{goods_count}

,addtime = #{addtime}


where 1=1

and cart_id = #{cart_id}


and user_id = #{user_id}


and goods_id = #{goods_id}

mapper.select:
select 

from 

where 1=1

and cart_id = #{cart_id}


and user_id = #{user_id}


and goods_id = #{goods_id}


and attribute_detail_id = #{attribute_detail_id}

select t1.goods_id, addtime, user_id, goods_count, cart_id, t1.attribute_detail_id, t1.goods_name, t1.goods_pic, t1.attribute_detail_name, t1.goods_price, if(t1.attribute_detail_id='1',t2.goods_num,attribute_detail_num) goods_num from shop_cart t1 left join v_market_shop_goods m on t1.goods_id = m.goods_id left join shop_goods t2 on t1.goods_id=t2.goods_id left join shop_attribute_detail t3 on t1.attribute_detail_id=t3.attribute_detail_id where 1=1
and m.market_id =#{market_id}
and t1.user_id =#{user_id} order by addtime desc

select IFNULL(sum(goods_count),0) from shop_cart t1 left join v_market_shop_goods m on t1.goods_id = m.goods_id left join shop_goods t2 on t1.goods_id=t2.goods_id left join shop_attribute_detail t3 on t1.attribute_detail_id=t3.attribute_detail_id where 1=1
and m.market_id =#{market_id}
and t1.user_id=#{user_id}
