mapper.update:
update

set 
status = #{status}
where 
coupon_id = #{coupon_id}
and user_id = #{user_id}

mapper.select:
select 

from 

where 1=1

and usercoupon_id = #{usercoupon_id}


and coupon_id = #{coupon_id}


and user_id = #{user_id}

select t1.coupon_id, t1.addtime, t1.user_id, t1.status, t1.usercoupon_id, t2.coupon_name, t2.coupon_price, t2.starttime, t2.endtime, t2.use_price from shop_usercoupon t1 left join shop_coupon t2 on t1.coupon_id=t2.coupon_id where status = 1 and user_id=#{user_id} and t2.use_price <= #{order_total} and endtime >= #{nowtime}
order by coupon_price desc
order by t1.addtime desc
