mapper.select:
SELECT
	id id,
	order_number orderNumber,
	balance_id balanceId,
	addtime addtime,
	total_price totalPrice,
	pay_way payWay,
	status status,
	seller_id sellerId,
	remark remark
	FROM balance_offline_order
	WHERE 1 = 1
	 and id = #{id}
	 and order_number = #{orderNumber}
	 and balance_id = #{balanceId}
	 and addtime = #{addtime}
	 and total_price = #{totalPrice}
	 and pay_way = #{payWay}
	 and status = #{status}
	 and seller_id = #{sellerId}
	 and remark = #{remark}

SELECT
	id id,
	order_number orderNumber,
	balance_id balanceId,
	addtime addtime,
	total_price totalPrice,
	pay_way payWay,
	status status,
	seller_id sellerId,
	remark remark
	FROM balance_offline_order
	WHERE 1 = 1 and status !=0 
	 and id = #{id}
	 and order_number = #{orderNumber}
	 and balance_id = #{balanceId}
	 and addtime = #{addtime}
	 and total_price = #{totalPrice}
	 and pay_way = #{payWay}
	 and status = #{status}
	 and seller_id = #{sellerId}
	 and remark = #{remark}
	ORDER BY addtime desc
