mapper.select:
SELECT
count(*)
FROM v_deliverer_cost
WHERE 1 = 1
and type_id = #{typeId}
and deliverer_id = #{delivererId}
and status = #{status}
and cost = #{cost}
and type = #{type}
and addtime = #{addtime}
and date(addtime)=curdate()
and date(addtime)=curdate()
and date(addtime)=DATE_SUB(curdate(),INTERVAL 1 DAY)
and YEARWEEK(date_format(addtime,'%Y-%m-%d')) = YEARWEEK(now())
and YEARWEEK(date_format(addtime,'%Y-%m-%d')) = YEARWEEK(now())-1
and date_format(addtime,'%Y-%m')=date_format(now(),'%Y-%m')
and date_format(addtime,'%Y-%m')=date_format(DATE_SUB(curdate(), INTERVAL 1
    MONTH),'%Y-%m')

SELECT
type_id typeId,
deliverer_id delivererId,
status status,
cost cost,
type type,
addtime addtime
FROM v_deliverer_cost
WHERE 1 = 1
and type_id = #{typeId}
and deliverer_id = #{delivererId}
and status = #{status}
and cost = #{cost}
and type = #{type}
and addtime = #{addtime}
