mapper.select:
SELECT
	id id,
	deliverer_id delivererId,
	money_changed moneyChanged,
	changed_reason changedReason,
	fee fee,
	update_time updateTime,
	operator operator,
	create_time createTime,
	remark remark,
	auditor auditor,
	audit_time auditTime
	FROM deliverer_wallet_detail
	WHERE 1 = 1
	 and id = #{id}
	 and deliverer_id = #{delivererId}
	 and money_changed = #{moneyChanged}
	 and changed_reason = #{changedReason}
	 and fee = #{fee}
	 and update_time = #{updateTime}
	 and operator = #{operator}
	 and create_time = #{createTime}
	 and remark = #{remark}
	 and auditor = #{auditor}
	 and audit_time = #{auditTime}

