mapper.select:
SELECT
	count(*)
	FROM shop_goods
	WHERE 1 = 1
	 and goods_id = #{goodsId}
	 and goods_pic = #{goodsPic}
	 and goods_name = #{goodsName}
	 and goods_title = #{goodsTitle}
	 and goods_price = #{goodsPrice}
	 and goods_num = #{goodsNum}
	 and spec_name = #{specName}
	 and spec_title = #{specTitle}
	 and spec_price = #{specPrice}
	 and goods_detail = #{goodsDetail}
	 and goods_sales = #{goodsSales}
	 and sort = #{sort}
	 and category_id = #{categoryId}
	 and tuijian = #{tuijian}
	 and reason_return = #{reasonReturn}
	 and sell_count = #{sellCount}

SELECT
	goods_id goodsId,
	goods_pic goodsPic,
	goods_name goodsName,
	goods_title goodsTitle,
	goods_price goodsPrice,
	goods_num goodsNum,
	spec_name specName,
	spec_title specTitle,
	spec_price specPrice,
	goods_detail goodsDetail,
	goods_sales goodsSales,
	sort sort,
	category_id categoryId,
	tuijian tuijian,
	reason_return reasonReturn,
	sell_count sellCount
	FROM shop_goods
	WHERE 1 = 1
	 and goods_id = #{goodsId}
	 and goods_pic = #{goodsPic}
	 and goods_name = #{goodsName}
	 and goods_title = #{goodsTitle}
	 and goods_price = #{goodsPrice}
	 and goods_num = #{goodsNum}
	 and spec_name = #{specName}
	 and spec_title = #{specTitle}
	 and spec_price = #{specPrice}
	 and goods_detail = #{goodsDetail}
	 and goods_sales = #{goodsSales}
	 and sort = #{sort}
	 and category_id = #{categoryId}
	 and tuijian = #{tuijian}
	 and reason_return = #{reasonReturn}
	 and sell_count = #{sellCount}

SELECT
	goods_id goodsId,
	goods_pic goodsPic,
	goods_name goodsName,
	goods_title goodsTitle,
	goods_price goodsPrice,
	goods_num goodsNum,
	spec_name specName,
	spec_title specTitle,
	spec_price specPrice,
	goods_detail goodsDetail,
	goods_sales goodsSales,
	sort sort,
	category_id categoryId,
	tuijian tuijian,
	reason_return reasonReturn,
	sell_count sellCount
	FROM shop_goods
	WHERE 1 = 1
	 and goods_id = #{goodsId}
	 and goods_pic = #{goodsPic}
	 and goods_name = #{goodsName}
	 and goods_title = #{goodsTitle}
	 and goods_price = #{goodsPrice}
	 and goods_num = #{goodsNum}
	 and spec_name = #{specName}
	 and spec_title = #{specTitle}
	 and spec_price = #{specPrice}
	 and goods_detail = #{goodsDetail}
	 and goods_sales = #{goodsSales}
	 and sort = #{sort}
	 and category_id = #{categoryId}
	 and tuijian = #{tuijian}
	 and reason_return = #{reasonReturn}
	 and sell_count = #{sellCount}
	 and goods_name like '%${searchGoodsName}%'
	ORDER BY sort ASC
