mapper.select:
SELECT
	order_detail_id orderDetailId,
	goods_id goodsId,
	goods_pic goodsPic,
	goods_name goodsName,
	goods_price goodsPrice,
	goods_count goodsCount,
	goods_total goodsTotal,
	attribute_detail_id attributeDetailId,
	attribute_detail_name attributeDetailName,
	pay_total payTotal,
	order_id orderId,
	sort sort,
	status status,
	seller_id sellerId,
	seller_seize_time sellerSeizeTime,
	seller_prepare_time sellerPrepareTime,
	deliverer_receive_time delivererReceiveTime,
	avg_price avgPrice
	FROM shop_order_detail
	WHERE 1 = 1
	 and order_detail_id = #{orderDetailId}
	 and goods_id = #{goodsId}
	 and goods_pic = #{goodsPic}
	 and goods_name = #{goodsName}
	 and goods_price = #{goodsPrice}
	 and goods_count = #{goodsCount}
	 and goods_total = #{goodsTotal}
	 and attribute_detail_id = #{attributeDetailId}
	 and attribute_detail_name = #{attributeDetailName}
	 and pay_total = #{payTotal}
	 and order_id = #{orderId}
	 and sort = #{sort}
	 and status = #{status}
	 and seller_id = #{sellerId}
	 and seller_seize_time = #{sellerSeizeTime}
	
	 and seller_prepare_time = #{sellerPrepareTime}
	 and deliverer_receive_time = #{delivererReceiveTime}

