mapper.select:
        SELECT
        order_id orderId,
        addtime addtime,
        total_price totalPrice,
        order_total orderTotal,
        coupon_price couponPrice,
        coupon_id couponId,
        freight_price freightPrice,
        pay_way payWay,
        user_id userId,
        addr_realname addrRealname,
        addr_phone addrPhone,
        addr_city addrCity,
        address address,
        ip_address ipAddress,
        user_ip userIp,
        status status,
        express_title expressTitle,
        express_name expressName,
        express_num expressNum,
        transaction_id transactionId,
        market_id marketId,
        deliverer_id delivererId,
        delivery_cost deliveryCost,
        cancel_time cancelTime,
        delivery_finish_time deliveryFinishTime,
        finish_time finishTime,
        location location,
        delivery_time_slice deliveryTimeSlice
        FROM shop_order
        WHERE 1 = 1
        and order_id = #{orderId}
        and addtime = #{addtime}
        and total_price = #{totalPrice}
        and order_total = #{orderTotal}
        and coupon_price = #{couponPrice}
        and coupon_id = #{couponId}
        and freight_price = #{freightPrice}
        and pay_way = #{payWay}
        and user_id = #{userId}
        and addr_realname = #{addrRealname}
        and addr_phone = #{addrPhone}
        and addr_city = #{addrCity}
        and address = #{address}
        and ip_address = #{ipAddress}
        and user_ip = #{userIp}
        and status = #{status}
        and express_title = #{expressTitle}
        and express_name = #{expressName}
        and express_num = #{expressNum}
        and transaction_id = #{transactionId}
        and market_id = #{marketId}
        and deliverer_id = #{delivererId}

        and delivery_cost = #{deliveryCost}
        and cancel_time = #{cancelTime}
        and delivery_finish_time = #{deliveryFinishTime}
        and finish_time = #{finishTime}

        SELECT
        order_id orderId,
        addtime addtime,
        total_price totalPrice,
        order_total orderTotal,
        coupon_price couponPrice,
        coupon_id couponId,
        freight_price freightPrice,
        pay_way payWay,
        user_id userId,
        addr_realname addrRealname,
        addr_phone addrPhone,
        addr_city addrCity,
        address address,
        ip_address ipAddress,
        user_ip userIp,
        status status,
        express_title expressTitle,
        express_name expressName,
        express_num expressNum,
        transaction_id transactionId,
        market_id marketId,
        deliverer_id delivererId,
        delivery_cost deliveryCost,
        cancel_time cancelTime,
        delivery_finish_time deliveryFinishTime,
        finish_time finishTime,
        location location,
        delivery_time_slice deliveryTimeSlice,
        remark remark
        FROM shop_order
        WHERE 1 = 1
        and order_id = #{orderId}
        and addtime = #{addtime}
        and total_price = #{totalPrice}
        and order_total = #{orderTotal}
        and coupon_price = #{couponPrice}
        and coupon_id = #{couponId}
        and freight_price = #{freightPrice}
        and pay_way = #{payWay}
        and user_id = #{userId}
        and addr_realname = #{addrRealname}
        and addr_phone = #{addrPhone}
        and addr_city = #{addrCity}
        and address = #{address}
        and ip_address = #{ipAddress}
        and user_ip = #{userIp}
        and status in (5,25)
        and status = #{status}
        and express_title = #{expressTitle}
        and express_name = #{expressName}
        and express_num = #{expressNum}
        and transaction_id = #{transactionId}
        and market_id = #{marketId}
        and deliverer_id = #{delivererId}

        and delivery_cost = #{deliveryCost}
        and cancel_time = #{cancelTime}
        and delivery_finish_time = #{deliveryFinishTime}
        and finish_time = #{finishTime}

        and date(addtime) = curdate()

        and (order_id like '%${searchString}%' or addr_phone like '%${searchString}%')
        
        ORDER BY addtime desc
